            public string ReducirEspaciado(string Cadena)
            {
                while (Cadena.Contains("  "))
                {
                    Cadena = Cadena.Replace("  ", " ");
                }
                while (Cadena.Contains(" "))
                {
                    Cadena = Cadena.Replace(" ", "_");
                }
                return Cadena;
            }
